CREATE TABLE "public"."recipes" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "title" text NOT NULL, "description" text NOT NULL, "image_url" text NOT NULL, "category_id" uuid NOT NULL, "user_id" uuid NOT NULL, "created_at" timestamptz NOT NULL DEFAULT now(), "updated_at" timestamptz NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("user_id") REFERENCES "public"."users"("id") ON UPDATE restrict ON DELETE cascade, FOREIGN KEY ("category_id") REFERENCES "public"."categories"("id") ON UPDATE restrict ON DELETE no action, UNIQUE ("id"), UNIQUE ("user_id"), UNIQUE ("category_id"));
CREATE EXTENSION IF NOT EXISTS pgcrypto;
